import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:intalim/app/router.dart';import 'package:intalim/common/widgets/marquee_widget.dart';class CustomAppBar extends StatelessWidget {  final String eduTypeLessonName;  const CustomAppBar({super.key, required this.eduTypeLessonName});  @override  Widget build(BuildContext context) {    return Card(      elevation: 2.0, // cardElevation      shape: RoundedRectangleBorder(        borderRadius: BorderRadius.circular(0.0), // Set your desired border radius      ),      child: Container(        height: kToolbarHeight, // Match Android's actionBarSize        child: Row(          children: [            InkWell(              onTap: () {                finish(context);              },              child: Container(                padding: EdgeInsets.all(16.0),                child: Image.asset('assets/images/back_btn.png'),              ),            ),            Expanded(              child: SingleChildScrollView(                scrollDirection: Axis.horizontal,                child: MarqueeWidget(                  direction: Axis.horizontal,                    child: Text(                  eduTypeLessonName,                  style: TextStyle(                    fontFamily: 'Montserrat',                    color: Colors.black,                    fontSize: 16.0,                    fontWeight: FontWeight.w600,                  ),                )),              ),            ),            // Add other widgets as needed          ],        ),      ),    );  }}