import 'dart:convert';import 'package:floor/floor.dart';import '../../lessons/model/lesson_data.dart';@entityclass ContentData {  @primaryKey  int? id;  int? typeId;  int? topicId;  String? content;  ContentData({required this.id, this.typeId, this.topicId, this.content});  ContentData.fromJson(Map<String, dynamic> json) {    id = json['id'];    typeId = json['type_id'];    topicId = json['topic_id'];    content = jsonEncode(Name.fromJson(json["content"]).toJson());  }  Map<String, dynamic> toJson() {    final Map<String, dynamic> data = <String, dynamic>{};    data['id'] = id;    data['type_id'] = typeId;    data['topic_id'] = topicId;    data['content'] = content;    return data;  }}