import 'package:flutter/material.dart';import '../../../common/services/utils/utils.dart';class MainScreen extends StatefulWidget {  const MainScreen({super.key});  @override  State<MainScreen> createState() => _MainScreenState();}class _MainScreenState extends State<MainScreen> {  PageController _pageController = PageController();  int _currentIndex = 0;  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        title: Text('Your App Title'),      ),      body: PageView.builder(        controller: _pageController,        onPageChanged: (index) {          setState(() {            _currentIndex = index;          });        },        itemCount: 4, // Number of pages in ViewPager        itemBuilder: (context, index) {          // Return each page based on the index          return buildPage(index);        },      ),      bottomNavigationBar: BottomNavigationBar(        currentIndex: _currentIndex,        onTap: (index) {          setState(() {            _currentIndex = index;            _pageController.jumpToPage(index);          });        },        selectedItemColor: Utils.colorPrimary,        // Set selected label color        unselectedItemColor: Utils.defTextColor,        selectedIconTheme: IconThemeData(color: Utils.colorPrimary),        showUnselectedLabels: true,        items: [          BottomNavigationBarItem(            icon: Image.asset("assets/images/home.png", width: 24, height: 24, color: _currentIndex == 0 ? Utils.colorPrimary : Utils.defTextColor),            label: 'Home',          ),          BottomNavigationBarItem(            icon: Image.asset("assets/images/lessons_main.png", width: 24, height: 24, color: _currentIndex == 1 ? Utils.colorPrimary : Utils.defTextColor),            label: 'Lessons',          ),          BottomNavigationBarItem(            icon: Image.asset("assets/images/saved_main.png", width: 24, height: 24, color: _currentIndex == 2 ? Utils.colorPrimary : Utils.defTextColor),            label: 'Saved',          ),          BottomNavigationBarItem(            icon: Image.asset(_currentIndex == 3 ? "assets/images/tests_main_selected.png" : "assets/images/tests_main.png", width: 24, height: 24),            label: 'Tests',          ),        ],      ),    );  }  Widget buildPage(int index) {    // Customize each page based on the index    return Container(      color: Colors.blue, // Change background color as needed      child: Center(        child: Text(          'Page ${index + 1}',          style: TextStyle(fontSize: 20, color: Colors.white),        ),      ),    );  }}