import 'dart:convert';import 'package:floor/floor.dart';import '../../lessons/model/lesson_data.dart';@entityclass SavedLessonData {  @primaryKey  int? id;  int? topicId;  String? topic;  int? readingTime;  int? timeLimit;  SavedLessonData({required this.id,  this.topicId, this.topic, this.readingTime, this.timeLimit});  SavedLessonData.fromJson(Map<String, dynamic> json) {    id = json['id'];    topicId = json['topic_id'];    topic = jsonEncode(Name.fromJson( json['topic']["name"]).toJson());    readingTime = json["topic"]['student_topic_action'] != null ? json["topic"]['student_topic_action']['reading_time'] ?? 0 : 0;    timeLimit = json["topic"]['topic_action_limit'] != null ? json["topic"]['topic_action_limit']['time_limit'] ?? 1 : 1;  }  Map<String, dynamic> toJson() {    final Map<String, dynamic> data = <String, dynamic>{};    data['id'] = id;    data['topic_id'] = topicId;    data['topic'] = topic;    data['reading_time'] = readingTime;    data['time_limit'] = timeLimit;    return data;  }}