import 'dart:convert';import 'package:floor/floor.dart';import '../../lessons/model/lesson_data.dart';@entityclass TopicData {  @primaryKey  int? id;  int? lessonId;  int? readingTime;  int? timeLimit;  String? name;  TopicData({required this.id, this.lessonId, this.readingTime, this.timeLimit, required this.name});  TopicData.fromJson(Map<String, dynamic> json) {    id = json['id'];    lessonId = json['id'] ?? 0;    readingTime = json['student_topic_action'] != null ? json['student_topic_action']['reading_time'] ?? 0 : 0;    timeLimit = json['topic_action_limit'] != null ? json['topic_action_limit']['time_limit'] ?? 1 : 1;    name = jsonEncode(Name.fromJson(json["name"]).toJson());  }  Map<String, dynamic> toJson() {    final Map<String, dynamic> data = <String, dynamic>{};    data['id'] = id;    data['lessonId'] = lessonId;    data['readingTime'] = readingTime;    data['timeLimit'] = timeLimit;    data['name'] = name;    return data;  }}